import { createSlice } from "@reduxjs/toolkit";

const DUMMY_EVENTS = [
  {
    id: "e1",
    title: "Quadrantid Meteor Shower",
    date: "Jan. 3–4",
    url: "https://imageio.forbes.com/specials-images/imageserve/62e8e87ebae872f3f3373b0b/Gemini-meteor-shower/960x0.jpg?format=jpg&width=960",
    description:
      "The Quadrantids (QUA) are a meteor shower that peaks in early January and whose radiant lies in the constellation Boötes. The zenithal hourly rate (ZHR) of this shower can be as high as that of two other reliably rich meteor showers, the Perseids in August and the Geminids in December,[4] yet Quadrantid meteors are not seen as often as those of the two other showers because the time frame of the peak is exceedingly narrow, sometimes lasting only hours. Moreover, the meteors are quite faint, with mean apparent magnitudes between 3.0 and 6.0.",
  },
  {
    id: "e2",
    title: "Perseid Meteoric Shower",
    date: "11-12-13 Agust",
    url: "https://cdn.mos.cms.futurecdn.net/9xUxE2FptNBRT6zgXUVWx7-970-80.jpg.webp",
    description:
      "The Perseids are a prolific meteor shower associated with the comet Swift–Tuttle. The meteors are called the Perseids because the point from which they appear to hail (called the radiant) lies in the constellation Perseus. The name is derived from the word Perseidai (Greek: Περσείδαι), the sons of Perseus in Greek mythology.",
  },
  {
    id: "e3",
    title: "Ursid Meteoric Shower",
    date: "Aug. 11–13",
    url: "https://www.bizsiziz.com/wp-content/uploads/2022/08/Perseid-Meteor-Shower-Time-Lapse-Photo.jpg",
    description:
      "The Ursid (URS) meteor activity begins annually around December 17 and runs for over a week, until the 25th or 26th. This meteor shower is named for its radiant point, which is located near the star Beta Ursae Minoris (Kochab) in the constellation Ursa Minor.",
  },
  {
    id: "e4",
    title: "Geminid Meteoric Shower",
    date: "Dec. 13–14",
    url: "https://expatguideturkey.com/wp-content/uploads/2022/08/can-2022-perseid-meteor-shower-be-watched-from-turkey-780x470.jpg",
    description:
      'The Geminids are a prolific meteor shower caused by the object 3200 Phaethon,[4] which is thought to be a Palladian asteroid[5] with a "rock comet" orbit.[6] This would make the Geminids, together with the Quadrantids, the only major meteor showers not originating from a comet. The meteors from this shower are slow moving, can be seen in December and usually peak around December 4–16, with the date of highest intensity being the morning of December 14. ',
  },
  ,
  {
    id: "e5",
    title: "Eta Aquarid Meteoric Shower",
    date: "May 4–5",
    url: "https://travelnevada.com/wp-content/uploads/2020/05/Perseids-1024x576.jpg",
    description:
      'The Geminids are a prolific meteor shower caused by the object 3200 Phaethon,[4] which is thought to be a Palladian asteroid[5] with a "rock comet" orbit.[6] This would make the Geminids, together with the Quadrantids, the only major meteor showers not originating from a comet. The meteors from this shower are slow moving, can be seen in December and usually peak around December 4–16, with the date of highest intensity being the morning of December 14. ',
  },
  ,
  {
    id: "e6",
    title: "Delta Aquarid Meteoric Shower",
    date: "July 29-30",
    url: "https://mymodernmet.com/wp/wp-content/uploads/2021/07/perseid-meteor-shower-01.jpg",
    description:
      "The Southern Delta Aquariids are a meteor shower visible from mid July to mid August each year with peak activity on 28 or 29 July. The Comet of origin is not known with certainty. Suspected candidate is Comet 96P Machholz. Earlier, it was thought to have originated from the Marsden and Kracht Sungrazing comets.",
  },
  {
    id: "e4",
    title: "Geminid Meteoric Shower",
    date: "Dec. 13–14",
    url: "https://expatguideturkey.com/wp-content/uploads/2022/08/can-2022-perseid-meteor-shower-be-watched-from-turkey-780x470.jpg",
    description:
      'The Geminids are a prolific meteor shower caused by the object 3200 Phaethon,[4] which is thought to be a Palladian asteroid[5] with a "rock comet" orbit.[6] This would make the Geminids, together with the Quadrantids, the only major meteor showers not originating from a comet. The meteors from this shower are slow moving, can be seen in December and usually peak around December 4–16, with the date of highest intensity being the morning of December 14. ',
  },
];

const initialState = {
  data: DUMMY_EVENTS,
};

const eventsSlice = createSlice({
  name: "events",
  initialState,
  reducers: {
    addEvent: (state, action) => {},

    removeEvent: (state, action) => {},

    editEvent: (state, action) => {},
  },
});

export const { addEvent, editEvent, removeEvent } = eventsSlice.actions;

export default eventsSlice.reducer;
